import React, { useState, useEffect } from "react";

// Define a list of empty image objects with placeholder titles for now
const images = [
  { src: "/images/lebronpic.webp", title: "James" },
  { src: "/images/lebronpic.webp", title: "Lebron James" },
  { src: "/images/lebronpic.webp", title: "Lebron" }
];

const Home = () => {
  const [current, setCurrent] = useState(0); // Track current image index

  useEffect(() => {
    // Automatically change slide every 5 seconds
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval); // Clear interval on unmount
  }, []);

  // Function to go to the next slide
  const nextSlide = () => setCurrent((current + 1) % images.length);

  // Function to go to the previous slide
  const prevSlide = () => setCurrent((current - 1 + images.length) % images.length);

  return (
    <div className="w-full">
      {/* Slideshow container with background color from palette */}
      <div className="relative w-full h-[400px] overflow-hidden bg-[#B3E9C7]">
        {images.map((image, index) => (
          <div
            key={index}
            className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
              index === current ? "opacity-100 z-10" : "opacity-0 z-0"
            }`}
          >
            {/* Simulated image area with gradient fade effect */}
            <div className="w-full h-full bg-[#C2F8CB] relative">
              {/* Overlay: fade gradient from bottom to top */}
              <div className="absolute inset-0 bg-gradient-to-t from-[#5603AD]/80 via-transparent to-transparent"></div>

              {/* Title text centered near the bottom */}
              <div className="absolute bottom-12 left-1/2 transform -translate-x-1/2 text-white text-3xl font-bold drop-shadow-md text-center">
                {image.title || "Your Title Here"}
              </div>
            </div>
          </div>
        ))}

        {/* Navigation dots/buttons for manual slide switching */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-4">
          <button
            onClick={prevSlide}
            className="w-4 h-4 rounded-full bg-white/70 hover:bg-white"
          ></button>
          <button
            onClick={nextSlide}
            className="w-4 h-4 rounded-full bg-white/70 hover:bg-white"
          ></button>
        </div>
      </div>

      {/* Section header for intro blocks */}
      <div className="text-center mt-10 text-[#5603AD] text-2xl font-semibold">
        Brief Intro
      </div>

      {/* Grid of 4 intro boxes with placeholder content */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 px-10 py-8 text-center">
        {Array(4).fill("").map((_, i) => (
          <div key={i}>
            <div className="bg-[#C2F8CB] w-full h-32 rounded-xl"></div>
            <div className="mt-2 text-[#5603AD] font-medium">Brief Intro</div>
          </div>
        ))}
      </div>

      {/* Large highlight feature block below intro */}
      <div className="px-10 pb-20">
        <div className="bg-[#C2F8CB] h-60 rounded-xl"></div>
      </div>
    </div>
  );
};

export default Home;
